function openMovieDetails(e){document.querySelector("#myNavigator").pushPage("movie_details.html",{data:{title:e}})}function openHome(){document.querySelector("#myNavigator").pushPage("home_splitter.html")}function goBack(){document.querySelector("#menu").close().then(function(){document.querySelector("#myNavigator").popPage()})}ons.ready(function(){document.addEventListener("init",function(e){var t=e.target;"home"===t.id?(t.querySelector("#menu_button").onclick=function(){document.querySelector("#menu").open()},t.querySelector("#carousel").addEventListener("postchange",function(){t.querySelector("#dot0").classList.remove("circle_current"),t.querySelector("#dot1").classList.remove("circle_current"),t.querySelector("#dot2").classList.remove("circle_current"),t.querySelector("#dot"+t.querySelector("#carousel").getActiveIndex()).classList.add("circle_current")})):"movie_details"===t.id&&(t.querySelector("#movie_title").innerHTML=t.data.title)})});